import React, {Component} from 'react'
import './App.css'
import Upload from './upload/Upload'
import Dropzone from './dropzone/src/dropzone_comp/Dropzone'

class App extends Component {
  constructor(props){
    super(props);
    this.state = {
      uploadCompShown: true
    };

    this.showUpload = this.showUpload.bind(this);
    this.showDropzone = this.showDropzone.bind(this);
    this.renderComponent = this.renderComponent.bind(this);
  }

  showUpload(){
    this.setState({uploadCompShown:true});
  }

  showDropzone(){
    this.setState({uploadCompShown:false});
  }

  renderComponent(){
    if(this.state.uploadCompShown){
      return(
        <Upload/>
      )
    }
    return(
      <Dropzone/>
    )

  }

  render(){
    return (
      <div className = "App">
        <img
          className="SLILogo"
          alt="sign language interpreter logo"
          src="581-582_Project_Logo.jpg"
          style={{
            opacity:0.8
          }}
        />
        <div><button onClick = {this.state.uploadCompShown ? this.showDropzone : this.showUpload}>Switch Component</button></div>
        <div className = "Card">
          {this.renderComponent()}
        </div>
      </div>
    )
  }
}

export default App;
